public static void printEvenOrOdd(int number) {
  // 引数が偶数か奇数かを判断
  if (number % 2 == 0) {
    System.out.println(number + " is even");
  } else {
    System.out.println(number + " is odd");
  }
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printEvenOrOdd(5);
  // 出力: 5 is odd
}

public static void repeatText(String text, int times) {
  // 文字列を指定回数繰り返す
  for (int i = 0; i < times; i++) {
    System.out.println(text);
  }
}

public static void main(String[] args) {
  // メソッドを呼び出し
  repeatText("Hello, Java!", 3);
  // 出力:
  // Hello, Java!
  // Hello, Java!
  // Hello, Java!
}

public static void printArraySum(int[] numbers) {
  // 合計値を計算
  int sum = 0;
  for (int number : numbers) {
    sum += number;
  }
  // 合計値を表示
  System.out.println(sum);
}

public static void main(String[] args) {
  // メソッドを呼び出し
  int[] array = {1, 2, 3, 4, 5};
  printArraySum(array);
  // 出力: 15
}

public static void printVarargsSum(int... numbers) {
  // 合計値を計算
  int sum = 0;
  for (      int number : numbers) {
    sum += number;
  }
  // 合計値を表示
  System.out.println(sum);
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printVarargsSum(1, 2, 3, 4, 5);
  // 出力: 15
}

