public static void createAndPrintArray(int length) {
  // 配列を作成
  int[] array = new int[length];

  // 配列の要素にインデックスを格納
  for (int i = 0; i < length; i++) {
    array[i] = i;
  }

  // 配列を表示
  for (int element : array) {
    System.out.println(element);
  }
}

public static void main(String[] args) {
  // メソッドを呼び出し
  createAndPrintArray(5);
  // 出力:
  // 0
  // 1
  // 2
  // 3
  // 4
}

public static void printPositiveNegativeOrZero(int number) {
  // 引数が正の数か負の数か0かを判断
  if (number > 0) {
    System.out.println(number + " is positive");
  } else if (number < 0) {
    System.out.println(number + " is negative");
  } else {
    System.out.println(number + " is zero");
  }
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printPositiveNegativeOrZero(-2);
  // 出      力: -2 is negative
}

public static void printEvenOrOdd(int number) {
