public static void printInRange(int num1, int num2) {
  // 引数が範囲内か判断
  if (num1 >= num2 - 10 && num1 <= num2 + 10) {
    System.out.println(num1 + " is in range of " + num2);
  } else {
    System.out.println(num1 + " is out of range of " + num2);
  }
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printInRange(25, 30);
  // 出力: 25 is in range of 30
}

public static void printMax(int num1, int num2) {
  // 最大値を取得
  int max = num1 > num2 ? num1 : num2;

  // 最大値を表示
  System.out.println("Max: " + max);
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printMax(12, 8);
  // 出力: Max: 12
}

public static void printMin(int num1, int num2) {
  // 最小値を取得
  int min = num1 < num2 ? num1 : num2;

  // 最小値を表示
  System.out.println("Min: " + min);
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printMin(12, 8);
  // 出力: Min: 8
}

public static void printSumInRange(int num1, int num2) {
  int sum = 0;
  // 引数の範囲内の整数の和を計算
  for (int i = num1; i <= num2; i++) {
    sum += i;
  }

  // 和を表示
  System.out.println("Sum: " + sum);
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printSumInRange(1, 5);
  // 出力: Sum: 15
}

public static void printAverage(int num1, int num2) {
  // 平均値を計算
  double average = (num1 + num2) / 2.0;

  // 平均値を表示
  System.out.println("Average: " + average);
}

public static void main(String[] args) {
  // メソッドを呼び出し
  printAverage(4, 8);
  // 出力: Average: 6.0
}
