public class Main {
    public static void main(String[] args) {
        int x = 2; // xを2に設定

        switch (x) {
            case 1:
                System.out.println("one"); // xが1の場合にメッセージを出力
                break;
            case 2:
                System.out.println("two"); // xが2の場合にメッセージを出力
                break;
            case 3:
                System.out.println("three"); // xが3の場合にメッセージを出力
                break;
            default:
                System.out.println("other"); // xが1, 2, 3以外の場合にメッセージを出力
        }
    }
}

