public class Main {
 public static void main(String[] args) {
 int[] arr = {1, 2, 3, 4, 5}; // 配列を設定

 for (int num : arr) { // 配列の要素を繰り返し処理
 if (num % 2 == 0) { // 偶数の場合
 System.out.println(num / 2); // 要素を2で割った値を出力
 } else { // 奇数の場合
 System.out.println(num + 1); // 要素に1を足した値を出力
 }
 }
 }
}

