// 文字列の定義
String str = "Hello, world!";
// "world"を"Java"に置換し、出力
System.out.println(str.replace("world", "Java"));
// Expected output: Hello, Java!

// 文字列の定義
String str = "Hello, world!";
// スペースで分割
String[] words = str.split(" ");
// 分割結果を出力
for (String word : words) {
    System.out.println(word);
}
// Expected output:
// Hello,
// world!

